import React, { useEffect, useState } from "react";
import './Components/style.css';
import { AnimeList } from "./Components/AnimeList";
import { AnimeInfo } from "./Components/AnimeInfo";
import { AddToList } from "./Components/AddToList";
import { RemoveFromList } from "./Components/RemoveFromList";
function App() {

  const [search,setSearch]=useState('Dragon')
  const [animeData,setAnimeData]=useState();
  const [animeInfo,setAnimeInfo]=useState()
  const [myAnimeList,setMyAnimeList]=useState([])

  const addTo=(anime)=>{
    const index=myAnimeList.findIndex((myanime)=>{
        return myanime.mal_id === anime.mal_id
    })
    if(index < 0){
      const newArray=[...myAnimeList,anime]
      setMyAnimeList(newArray);
    }

  }
  const removeFrom=(anime)=>{
    const newArray=myAnimeList.filter((myanime)=>{
      return myanime.mal_id !== anime.mal_id
    })
    setMyAnimeList(newArray)
  }
  const getData=async()=>{
      const res=await fetch(`https://api.jikan.moe/v4/anime?q=${search}&limit=20`)
      const resData= await res.json();
      setAnimeData(resData.data)
  }
  useEffect(()=>{
    getData()
  },[search])

  return (
    <main className="w-full overflow-hidden">
        <div className="w-full p-[2vw] bg-[#d3b191] flex justify-around items-center fixed top-0 text-white z-20">
          <h1 className="text-[2vw] font-medium">Jikan Anime</h1>
          <div className="w-full max-w-[30vw]">
              <input className="p-[0.7vw] focus:outline-none rounded-md text-[1vw] border-none w-full text-gray-600" type="search" placeholder="Search your anime" 
              onChange={(e)=>setSearch(e.target.value)}/>
          </div>
        </div>

        <div className="w-full flex justify-center mt-[10vw]">
        <main className="w-full z-50 h-screen bg-gray-700 absolute flex justify-center items-center opacity-60">
          <section className="bg-[#ffff] p-[3vw] rounded-xl w-[30vw] h-[20vw]">{animeInfo && <AnimeInfo animeInfo={animeInfo}/>}</section>
        </main>
          {/* <div className="w-full max-w-[20vw] h-full fixed left-0 top-[6vw]">
          </div> */}
          <div className="">
            <h2 className="text-black text-[1.5vw] font-semibold my-[1vw]">Most Trending</h2>
            <div className="grid grid-cols-4 gap-[2vw]">
                <AnimeList 
                animelist={animeData}
                setAnimeInfo={setAnimeInfo}
                animeComponent={AddToList}
                handleList={(anime)=>addTo(anime)}
                />
            </div>
            <h2 className="text-black text-[1.5vw] font-semibold my-[1vw]">My List</h2>
            <div className="grid grid-cols-4 gap-[2vw]">
                <AnimeList 
                animelist={myAnimeList}
                setAnimeInfo={setAnimeInfo}
                animeComponent={RemoveFromList}
                handleList={(anime)=>removeFrom(anime)}
                />
            </div>
          </div>
        </div>
    </main>
  );
}

export default App;
