import React from 'react'

export const AnimeList = ({ animelist,setAnimeInfo,animeComponent,handleList}) => {
  const AddToList=animeComponent;
  return (
    <>
      {
        animelist ? (
          animelist?.map((anime, index) => {
            return (
              <section className="w-full max-w-[16vw] h-[22vw] shadow-md text-center overflow-hidden relative" key={index} onClick={()=>setAnimeInfo(anime)}>
                <figure className='w-full h-[15vw]'>
                <img className='w-full h-full' src={anime?.images?.jpg?.large_image_url} alt="anime" />
                </figure>
                <div class="anime-info group">
                <h4 class="text-xl">{anime?.title}</h4>
                <div class="absolute bottom-0 w-full h-0 opacity-0 group-hover:h-full group-hover:opacity-100 transition duration-500">
                  <div class="bg-slate-300/90 px-4 py-2 rounded-t-[5vw] rounded-b-lg overflow-auto">
                    <h4>{anime?.title_japanese}</h4>
                    <h3>SYNOPSIS</h3>
                    <div class="synopsis">
                      <p>{anime?.synopsis}</p>
                    </div>
                    <AddToList />
                  </div>
                </div>
                </div>

              </section>
            )
          })
        ) : "Not Found"
      }

    </>
  )
}
